import mysql.connector as mysqlclass Database:    database = None    def connect(self, host = "localhost", user = "root", password = "", database = "pyplatform"):        if self.database is None:            self.database = mysql.connect(host=host, user=user, passwd=password, database=database)    def disconnect(self):        self.database.close()    def select(self, sql, values = ()):        return self.query(sql, values, "select")    def update(self, sql, values = ()):        return self.query(sql, values, "update")    def delete(self, sql, values = ()):        return self.query(sql, values, "delete")    def insert(self, sql, values = ()):        return self.query(sql, values, "insert")    def query(self, sql, values = (), type = "select"):        if self.database is None:            self.connect()        data = None        try:            cursor = self.database.cursor(dictionary=True)            cursor.execute(sql, values)            if type == "select":                data = cursor.fetchall()            cursor.close()        except mysql.Error as error:            print("Erreur lors de la requÃªte: {}".format(error))        return data    def commit(self):        self.database.commit()